import React, {useState} from 'react';
import './App.css';

import {Editor} from "react-draft-wysiwyg";
import {ContentState, convertToRaw, EditorState} from "draft-js";
import draftToHtml from "draftjs-to-html";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

const App = () => {


    const [content, setContent] = useState<string>('')
    const [editorState, setEditorState] = useState<EditorState>(EditorState.createWithContent(ContentState.createFromText('')))
    return (
        <Editor
            toolbarClassName="toolbarClassName"
            wrapperClassName="wrapperClassName"
            editorClassName="editorClassName"
            editorState={editorState}
            placeholder="Пишу хуйню на стэке PIDOR"
            onEditorStateChange={newState=>{
                setEditorState(newState)
                setContent(draftToHtml(convertToRaw(newState.getCurrentContent())))
            }}
            toolbar={{
                options:['inline','blockType','fontSize','list', 'textAlign', 'history', 'embedded', 'emoji', 'image'],
                inline:{isDropdown:true},
                list:{isDropdown:true},
                textAlign:{isDropdown:true},
                link:{isDropdown:true},
                history:{isDropdown:true},

            }}
        />
    );
};

export default App;
